mapper.select:
/* AdvertiseDAO.getAdvertiseBankDepositList */ select a.deposit_no , a.amount , a.bank_code , date_format(a.deposit_date, '%Y.%m.%d') as deposit_date , a.deposit_name , a.advertise_req_no , a.comment , a.reg_user_no , get_user_name(a.reg_user_no) as reg_user_name , (select title from tb_advertise_req where advertise_req_no = a.advertise_req_no) as advertise_title , a.udp_user_no , a.udp_date , a.mapping_user_no , a.mapping_date from tb_advertise_bank_deposit a where 1 = 1
and a.deposit_date = #{searchText}
and a.deposit_name like concat('%', #{searchText}, '%')
and a.advertise_req_no is not null
and a.advertise_req_no is null
order by a.deposit_no desc limit #{startNo}, #{pageScale}

/* AdvertiseDAO.getAdvertiseBankDepositCount */ select count(1) from tb_advertise_bank_deposit a where 1 = 1
and a.deposit_date = #{searchText}
and a.deposit_name like concat('%', #{searchText}, '%')
and a.advertise_req_no is not null
and a.advertise_req_no is null

mapper.insert:

/*	AdvertiseDAO.insertAdvertiseBankDeposit	*/
insert into tb_advertise_bank_deposit (
	amount, bank_code, deposit_date, deposit_name, comment, reg_user_no, reg_date
) values(
	#{amount}, #{bankCode}, #{depositDate}, #{depositName}, #{comment}, #{userNo}, now()
)


select last_insert_id()

mapper.update:

/*	AdvertiseDAO.updateAdvertiseBankDeposit	*/
update tb_advertise_bank_deposit set
	amount = #{amount}
	, bank_code = #{bankCode}
	, deposit_date = #{depositDate}
	, deposit_name = #{depositName}
	, comment = #{comment}
	, udp_user_no = #{userNo}
	, udp_date = now()
where
	deposit_no = #{depositNo}

mapper.delete:

/*	AdvertiseDAO.updateAdvertiseBankDeposit	*/
delete from tb_advertise_bank_deposit
where
	deposit_no = #{depositNo}

mapper.insert:

/*	AdvertiseDAO.insertAdvertiseBankDepositHist	*/
insert into tb_advertise_bank_deposit_hist (
	deposit_no, amount, bank_code, deposit_date, deposit_name, advertise_req_no, comment, reg_user_no, reg_date, udp_user_no, udp_date, mapping_user_no, mapping_date, delete_yn
)
select
	deposit_no, amount, bank_code, deposit_date, deposit_name, advertise_req_no, comment, reg_user_no, reg_date, udp_user_no, udp_date, mapping_user_no, mapping_date, #{deleteYn}
from
	tb_advertise_bank_deposit
where
	deposit_no = #{depositNo}


select last_insert_id()

mapper.select:

/*	AdvertiseDAO.checkAlreadyAdvertiseDeposit	*/
select count(1)
from tb_advertise_req
where
	advertise_req_no = #{advertiseReqNo}
	and deposit_yn = 'Y'


/*	AdvertiseDAO.checkDepositMapping	*/
select count(1)
from tb_advertise_bank_deposit
where
	deposit_no = #{depositNo}
	and advertise_req_no is not null

mapper.update:

/*	AdvertiseDAO.updateAdvertiseDeposit	*/
update tb_advertise_req set
	deposit_yn = #{depositYn}
where
	advertise_req_no = #{advertiseReqNo}


/*	AdvertiseDAO.updateAdvertiseDepositMappging	*/
update tb_advertise_bank_deposit set
	advertise_req_no = #{advertiseReqNo}
	, mapping_user_no = #{userNo}
	, mapping_date = now()
where
	deposit_no = #{depositNo}
	and advertise_req_no is null


/*	AdvertiseDAO.updateAdvertiseDepositMappgingCancel	*/
update tb_advertise_bank_deposit set
	advertise_req_no = null
	, mapping_user_no = null
	, mapping_date = null
where
	deposit_no = #{depositNo}
	and advertise_req_no = #{advertiseReqNo}

mapper.select:
/* AdvertiseDAO.advertiseReqList */ select c.advertise_req_no , c.title , c.banner_file_no , get_file_path(c.banner_file_no) as banner_file_path , c.cancel_yn , c.deposit_yn , c.banner_size , c.price , b.hakwon_name , c.stop_admin_yn , (select count(1) from tb_advertise_local_date x where x.advertise_req_no = c.advertise_req_no and x.view_month = date_format(now(), '%Y%m')) as view_count from tb_hakwon a , tb_hakwon_info b , tb_advertise_req c where c.hakwon_no = a.hakwon_no and a.hakwon_no = b.hakwon_no
and a.hakwon_no = #{hakwonNo}
and c.deposit_yn = #{depositYn}
and ( b.hakwon_name like concat('%', #{searchText}, '%') or c.title like concat('%', #{searchText}, '%') )
order by c.advertise_req_no desc limit #{startNo}, #{pageScale}

/* AdvertiseDAO.advertiseReqTotCount */ select count(1) from tb_hakwon a , tb_hakwon_info b , tb_advertise_req c where c.hakwon_no = a.hakwon_no and a.hakwon_no = b.hakwon_no
and a.hakwon_no = #{hakwonNo}
and c.deposit_yn = #{depositYn}
and ( b.hakwon_name like concat('%', #{searchText}, '%') or c.title like concat('%', #{searchText}, '%') )

mapper.update:

/*	AdvertiseDAO.updateAdvertiseStop	*/
update tb_advertise_req set
	stop_admin_yn = #{stopAdminYn}
where
	advertise_req_no = #{reqNo}
