where
	hakwon_no = #{hakwon_no}


/* HakwonDeleteDAO.delHakwonTeacher */
delete from tb_hakwon_teacher
where
	hakwon_no = #{hakwon_no}


/* HakwonDeleteDAO.delHakwonMember */
delete from tb_hakwon_member
where
	hakwon_no = #{hakwon_no}


/* HakwonDeleteDAO.delHakwon */
delete from tb_hakwon
where
	hakwon_no = #{hakwon_no}


/* HakwonDeleteDAO.delHakwonEvent */
delete from tb_event
where
	hakwon_no = #{hakwon_no}
