mapper.select:
/* ManagerDAO.managerList */ select a.user_no , a.user_type , a.user_id , a.user_email , a.approved_yn , a.use_yn , date_format(a.reg_date, '%Y.%m.%d') as reg_date , b.user_name , b.user_gender , c.manager_no from tb_user a , tb_user_info b , tb_manager c where a.user_type= '002' and a.approved_yn = 'Y' and a.user_no = b.user_no and a.user_no = c.user_no
and ( b.user_name like concat('%', #{searchText}, '%') or b.user_id like concat('%', #{searchText}, '%') or a.user_email like concat('%', #{searchText}, '%') )
order by a.reg_date desc , b.user_name asc limit #{startNo}, #{pageScale}

mapper.update:

/* ManagerDAO.approvedUserUpdate */
update tb_user set
	approved_yn = #{approvedYn}
where
	user_no in


${item}


/* ManagerDAO.approvedUserInfoUpdate */
update tb_user_info set
	approved_date = now()
	, approved_user_no = #{user_no}
where
	user_no in


${item}
