mapper.select:
/* UserDAO.unauthorizedUserList */ select a.user_no , a.user_type , a.user_id , a.user_email , a.approved_yn , a.use_yn , b.user_name , b.user_gender , b.tel1_no , get_file_path(b.photo_file_no) as user_photo_path , date_format(a.reg_date, '%Y.%m.%d') as reg_date from tb_user a , tb_user_info b where a.user_type= #{userType} and (a.approved_yn = 'N' or a.approved_yn is null) and a.user_no = b.user_no
and ( b.user_name like concat('%', #{searchText}, '%') or a.user_id like concat('%', #{searchText}, '%') or b.tel1_no like concat('%', #{searchText}, '%') )
order by a.reg_date desc , b.user_name asc limit #{startNo}, #{pageScale}

/* UserDAO.userInfo */ select a.user_no , a.user_id , b.user_name , a.user_email , case when b.user_gender = 'M' then '남' else '여' end as user_gender_name , b.tel1_no , get_file_path(b.photo_file_no) as photo_file_path , get_code_name('001', a.user_type) as user_type_name , calc_age(b.user_birthday) as user_age , date_format(a.reg_date, '%Y.%m.%d') as reg_date , a.use_yn , a.user_type , b.attendance_code from tb_user a , tb_user_info b where a.user_no = #{user_no}
and a.user_type= #{user_type}
and a.user_no = b.user_no

/* UserDAO.userList */ select a.user_no , a.user_type , get_code_name('001', a.user_type) as user_type_name , b.user_name , b.user_id , b.user_gender , get_file_path(b.photo_file_no) as photo_file_path , calc_age(b.user_birthday) as user_age from tb_user a , tb_user_info b where a.user_no = b.user_no
and (b.user_name like concat('%', #{searchText}, '%') or b.user_id like concat('%', #{searchText}, '%'))
and a.user_no = #{userNo}
order by b.user_name asc limit 50

mapper.update:

/*	UserDAO.userStopChange	*/
update tb_user set
	use_yn = #{use_yn}
where
	user_no = #{user_no}
