mapper.insert:

/*	AdvertiseDAO.insertAdvertiseReq	*/
insert into tb_advertise_req (
	hakwon_no, title, banner_file_no, banner_size, bank_info, stop_admin_yn, stop_master_yn, reg_user_no, reg_date
) values (
	#{hakwonNo}, #{title}, #{bannerFileNo}, #{bannerSize}, #{bankInfo}, 'N', 'N', #{regUserNo}, now()
)


select last_insert_id()

mapper.update:

/*	AdvertiseDAO.updateAdvertiseReqPrice	*/
update tb_advertise_req set
	price = #{totalPrice}
where
	advertise_req_no = #{advertiseReqNo}
	and reg_user_no = #{regUserNo}


/*	AdvertiseDAO.updateAdvertiseReq	*/
update tb_advertise_req set
	title = #{title}
	, banner_file_no = #{bannerFileNo}
where
	advertise_req_no = #{advertiseReqNo}
	and reg_user_no = #{userNo}


/*	AdvertiseDAO.updateAdvertiseReqCancel	*/
update tb_advertise_req set
	cancel_yn = 'Y'
	, cancel_date = now()
where
	advertise_req_no = #{reqNo}
	and reg_user_no = #{userNo}

mapper.insert:

/*	AdvertiseDAO.insertAdvertiseReqPayInfo	*/
insert into tb_advertise_req_pay_info (
	advertise_req_no, view_month, banner_size, price, dong_count, total_price
) values


(
	#{item.advertiseReqNo}
	, #{item.viewMonth}
	, #{item.bannerSize}
	, #{item.price}
	, #{item.dongCount}
	, #{item.totalPrice}
)


/*	AdvertiseDAO.insertAdvertiseLocal	*/
insert into tb_advertise_local_date (
	advertise_req_no, view_month, sido, gugun, dong, banner_size
) values


(
	#{item.advertiseReqNo}
	, #{item.viewMonth}
	, #{item.sido}
	, #{item.gugun}
	, #{item.dong}
	, #{item.bannerSize}
)

mapper.select:
/* AdvertiseDAO.advertiseReqList */ select c.advertise_req_no , c.title , c.banner_file_no , get_file_path(c.banner_file_no) as banner_file_path , c.cancel_yn , c.deposit_yn , c.banner_size , c.price , b.hakwon_name , (select count(1) from tb_advertise_local_date where advertise_req_no = c.advertise_req_no and date_format(now(), '%Y%m') = view_month) as view_month_count from tb_hakwon a , tb_hakwon_info b , tb_advertise_req c where a.master_user_no = #{userNo} and a.hakwon_no = b.hakwon_no
and a.hakwon_no = #{hakwonNo}
and a.hakwon_no = c.hakwon_no order by c.advertise_req_no desc limit #{startNo}, #{pageScale}


/*	AdvertiseDAO.advertiseReqTotCount	*/
select
	count(1)
from
	tb_hakwon a
	, tb_advertise_req b
where
	a.master_user_no = #{userNo}


	and a.hakwon_no = #{hakwonNo}


	and a.hakwon_no = b.hakwon_no

select.foreach:

/*	AdvertiseDAO.monthBannerPrice	*/
select
	#{item.adMonth} as adMonth
	, #{item.bannerSize} as bannerSize
	, a.price
from tb_advertise_price a
where
	a.price_year_month = #{item.adMonth}
	and a.banner_size = #{item.bannerSize}
