mapper.insert:

/*	eventDAO.eventInsert	*/
insert into tb_event (
	hakwon_no
	, event_title
	, event_content
	, event_status
	, reg_user_no
	, reg_date
	, begin_date
	, end_date
	, recommend_yn
	, add_info_title
) values (
	#{hakwon_no}
	, #{event_title}
	, #{event_content}
	, '001'
	, #{reg_user_no}
	, now()
	, #{begin_date}
	, #{end_date}
	, #{recommend_yn}
	, #{add_info_title}
)


select last_insert_id()

mapper.update:

/* eventDAO.eventUpdate */
update tb_event set
	udp_user_no = #{user_no}
	, udp_date = now()
	, event_title = #{event_title}
	, event_content = #{event_content}
	, begin_date = #{begin_date}
	, end_date = #{end_date}
	, recommend_yn = #{recommend_yn}
	, add_info_title = #{add_info_title}
where
	event_no = #{event_no}
	and event_status = '001'
	and reg_user_no = #{user_no}


/*eventDAO.eventDelete*/
update tb_event set
	event_status = '002'
	, udp_user_no = #{user_no}
	, udp_date = now()
where
	event_no = #{event_no}
	and reg_user_no = #{user_no}


/* eventDAO.eventPushSendUpdate */
update tb_event set
	push_send_yn = 'Y'
where
	event_no = #{event_no}
