mapper.select:
/* MessageViewDAO.sendGroupMessageList */ select c.title , a.user_no , b.user_name , date_format(c.send_date, '%Y.%m.%d %H:%i') as send_date , c.receiver_count , c.message_no , c.group_yn , (select count(1) from tb_file where file_parent_type = '002' and file_parent_no = c.message_no and file_use_yn = 'Y' and file_del_yn = 'N') as file_count , c.reservation_yn , date_format(c.reservation_date, '%Y.%m.%d %H:%i') as reservation_date , c.reservation_send_date from tb_user a , tb_user_info b , tb_message c where c.hakwon_no = #{hakwon_no} and c.send_user_no = #{user_no} and c.group_yn = 'Y' and a.user_no = b.user_no and a.user_no = c.send_user_no
and c.title like concat('%', #{searchText}, '%')
order by c.message_no desc limit #{startNo}, #{pageScale}

/* MessageViewDAO.sendSingleMessageList */ select c.title , a.user_no , b.user_name , date_format(c.send_date, '%Y.%m.%d %H:%i') as send_date , c.receiver_count , c.message_no , (select count(1) from tb_file where file_parent_type = '002' and file_parent_no = c.message_no and file_use_yn = 'Y' and file_del_yn = 'N') as file_count , (select count(1) from tb_content_reply where content_type = '002' and content_parent_no = d.receive_no) as reply_count , d.receive_no , c.group_yn , get_user_info(d.receive_user_no) as receive_user_info , date_format(d.receive_date, '%Y.%m.%d %H:%i') as receive_date , c.reservation_yn , date_format(c.reservation_date, '%Y.%m.%d %H:%i') as reservation_date , c.reservation_send_date from tb_user a , tb_user_info b , tb_message c , tb_message_receiver d where c.hakwon_no = #{hakwon_no} and c.send_user_no = #{user_no} and c.group_yn = 'N' and a.user_no = b.user_no and a.user_no = c.send_user_no and c.message_no = d.message_no
and c.title like concat('%', #{searchText}, '%')
order by c.message_no desc , d.receive_no asc limit #{startNo}, #{pageScale}

/* MessageViewDAO.userListInfo */ select b.user_name , b.user_no , b.user_id , a.user_type , c.code_name as user_type_name , get_file_path(b.photo_file_no) as user_photo_path from tb_user a , tb_user_info b , tb_code c where a.user_no in
#{item}
and a.user_no = b.user_no and c.code_group = '001' and a.user_type = c.code

/* MessageViewDAO.receiveMessageList */ select a.receive_no , a.message_no , b.send_user_no , date_format(b.send_date, '%Y.%m.%d %p %h:%i') as send_date , a.receive_user_no , date_format(a.receive_date, '%Y.%m.%d %p %h:%i') as receive_date , a.hakwon_no , b.title , b.content , b.preview_content , date_format(b.write_date, '%Y.%m.%d %p %h:%i') as write_date , (select count(1) from tb_content_reply where content_type = '002' and content_parent_no = a.receive_no) as reply_count , (select count(1) from tb_file where file_parent_type = '002' and file_parent_no = a.message_no and file_use_yn = 'Y' and file_del_yn = 'N') as file_count , a.receive_no , get_user_info(b.send_user_no) as send_user_info , get_user_info(a.receive_user_no) as receive_user_info from tb_message_receiver a , tb_message b where a.receive_user_no = #{receive_user_no}
and (a.hakwon_no = #{hakwon_no} or a.hakwon_no = -1)
and a.message_no = b.message_no order by a.message_no desc limit #{start_no}, #{page_scale}

mapper.update:

/* MessageDAO.updateMessageReceiveDate */
update tb_message_receiver set
	receive_date = now()
where
	receive_no = #{receiveNo}
	and receive_user_no = #{user_no}
	and receive_date is null

mapper.delete:

/* MessageViewDAO.deleteReservationMessage */
delete from tb_message
where
	message_no = #{messageNo}
	and send_user_no = #{user_no}
	and reservation_yn = 'Y'
	and reservation_send_date is null


/* MessageViewDAO.deleteMessage */
delete from tb_message
where
	message_no = #{messageNo}
	and send_user_no = #{user_no}


/* MessageViewDAO.deleteReceiveMessage */
delete from tb_message_receiver
where
	message_no = #{messageNo}
