mapper.select:
/* ParentDAO.parentList */ select a.user_no , a.user_type , a.user_id , a.user_email , a.approved_yn , a.use_yn , b.user_name , b.user_gender , b.tel1_no , get_file_path(b.photo_file_no) as user_photo_path , calc_age(b.user_birthday) as user_age , date_format(d.reg_date, '%Y년%m월%d일 %H:%i') as reg_date from tb_hakwon c , tb_hakwon_member d , tb_user a , tb_user_info b where c.hakwon_no = #{hakwon_no} and c.hakwon_no = d.hakwon_no and d.user_type= '005' and d.user_no = a.user_no and a.user_no = b.user_no
and b.user_name like concat('%', #{search_text}, '%')
order by d.reg_date desc limit #{start_no}, #{page_scale}


		/*	ParentDAO.classParentCount	*/
		select
			count(1)
 		from
	  		tb_hakwon_class_student a
	  		, tb_student_parent b
 		where
 			a.hakwon_no = #{hakwon_no}
 	  		and a.class_no = #{class_no}
 	  		and a.student_user_no = b.student_user_no
