mapper.insert:

/*	ReceiptDAO.insertReceipt	*/
insert into tb_receipt (
	reg_user_no, hakwon_no, student_no, receipt_amount, receipt_type
	, receipt_method, receipt_desc, receipt_date, reg_date
) values (
	#{userNo}, #{hakwonNo}, #{studentNo}, #{receiptAmount}, #{receiptType}
	, #{receiptMethod}, #{receiptDesc}, #{receiptDate}, now()
)


select last_insert_id()

mapper.select:
/* ReceiptDAO.selectStudent */ select a.user_no , a.user_id , a.user_email , b.user_name , b.user_gender , b.user_birthday , get_file_path(b.photo_file_no) as user_photo_path from tb_user a , tb_user_info b , tb_hakwon_member c where a.user_no = b.user_no and a.user_type = '006' and a.use_yn = 'Y' and a.approved_yn = 'Y'
and ( b.user_name like '%${studentName}%' or b.user_id like '%${studentName}%' )
and b.user_id = #{studentId}
and c.hakwon_no = #{hakwonNo} and c.user_no = a.user_no order by a.user_no asc

/* ReceiptDAO.selectReceiptList */ select a.receipt_no , a.reg_user_no , a.hakwon_no , a.student_no , a.receipt_amount , a.receipt_type , get_code_name('013', a.receipt_type) as receipt_type_name , get_code_name('013', a.receipt_method) as receipt_method_name , a.receipt_type , a.receipt_method , a.receipt_desc , a.receipt_date , DATE_FORMAT(a.reg_date,'%Y-%m-%d') as reg_date , c.user_name , c.user_id , get_file_path(c.photo_file_no) as user_photo_path from tb_receipt a , tb_user_info c
, tb_hakwon_class_student b
where a.hakwon_no = #{hakwonNo} and a.use_yn = 'Y' and a.student_no = c.user_no
and b.class_no = #{classNo} and b.student_user_no = a.student_no
and c.user_id like '%${searchText}%'
and c.user_name like '%${searchText}%'
and c.user_no = a.student_no
and a.receipt_type = #{receiptType}
and a.receipt_method = #{receiptMethod}
and a.receipt_date >= '${startDate} 00:00:00' and a.receipt_date < '${endDate} 00:00:00'
order by a.receipt_date desc, a.reg_date desc limit #{startNo}, #{pageScale}

/* ReceiptDAO.selectReceiptCount */ select count(a.receipt_no) from tb_receipt a
, tb_hakwon_class_student b
, tb_user_info c
where a.hakwon_no = #{hakwonNo}
and b.class_no = #{classNo} and b.student_user_no = a.student_no
and c.user_id like '%${searchText}%'
and c.user_name like '%${searchText}%'
and c.user_no = a.student_no
and a.receipt_type = #{receiptType}
and a.receipt_method = #{receiptMethod}
and a.receipt_date >= '${startDate} 00:00:00' and a.receipt_date < '${endDate} 00:00:00'
and a.use_yn = 'Y' order by a.receipt_date desc

/* ReceiptDAO.selectClassList */ select a.class_no , a.class_title from tb_hakwon_class a
, tb_hakwon_class_teacher b
where a.hakwon_no = #{hakwonNo}
and a.class_no = b.class_no and b.teacher_user_no = #{userNo}

mapper.update:

/*	receiptDao.updateReceipt	*/
update tb_receipt set
	student_no = #{studentNo}
	, receipt_amount = #{receiptAmount}
	, receipt_type = #{receiptType}
	, receipt_method = #{receiptMethod}
	, receipt_desc = #{receiptDesc}
	, receipt_date = #{receiptDate}
where
	receipt_no = #{receiptNo}
	and (
		reg_user_no = #{userNo}
		or 'true' = #{isWonjang}
	)

mapper.select:

/*	receiptDao.selectReceiptYear	*/
select
	a.receipt_month
from
	tb_receipt_month a
where
	a.hakwon_no = #{hakwonNo}
	and a.student_no = #{studentNo}
	and a.receipt_month >= #{startDate}
group by a.receipt_month
order by a.receipt_month
limit 12

mapper.insert:
/*	receiptDao.insertReceiptMonth	*/
insert into tb_receipt_month (
	receipt_no
	, hakwon_no
	, student_no
	, receipt_month
) values (
	#{idx}
	, #{hakwonNo}
	, #{studentNo}
	, #{receiptMonth}
)

mapper.delete:

/*	receiptDao.deleteReceipt	*/
delete from tb_receipt
where
	receipt_no = #{receiptNo}
	and (
		reg_user_no = #{userNo}
		or 'true' = #{isWonjang}
	)

/*	receiptDao.deleteReceiptMonth	*/
delete from tb_receipt_month
where
	receipt_no = #{receiptNo}

mapper.select:
/* receiptDao.selectReceiptYearList */ select a.user_no , a.receipt_date , b.user_id , b.user_name , get_file_path(b.photo_file_no) AS user_photo_path , ( select group_concat(y.class_title separator ',') from tb_hakwon_class_student x, tb_hakwon_class y where x.student_user_no = a.user_no and x.hakwon_no = a.hakwon_no and x.class_no = y.class_no ) as class_title
, get_receipt_month(#{thisYear}, a.hakwon_no, a.user_no) as month_n_date from tb_hakwon_member a , tb_user_info b
, tb_hakwon_class_student c
where a.hakwon_no = #{hakwonNo} and a.user_type = '006' and a.user_no = b.user_no
and a.hakwon_no = c.hakwon_no and c.class_no = #{classNo} and a.user_no = c.student_user_no
and a.receipt_date = #{registDay}
and b.user_id like concat('%', #{searchText}, '%')
and b.user_name like concat('%', #{searchText}, '%')
order by b.user_name asc limit ${startNo}, ${pageScale}
