mapper.update:
/*	SignUpDAO.updateForUserPasswd	*/
update tb_user set
	user_password = #{user_password}
where
	user_id = #{user_id}

mapper.select:

/* SignUpDAO.checkUserId */
select count(1)
from tb_user a
where a.user_id = #{user_id}


/* SignUpDAO.checkEmail */
select count(1)
from tb_user a
where a.user_email = #{user_email}

mapper.insert:

/* SignUpDAO.insertUser */
insert into tb_user (
	user_type, user_id, user_email, user_password, use_yn, reg_date


	, approved_yn


) values (
	#{user_type}, #{user_id}, #{user_email}, #{user_password}, 'Y', now()


	, 'Y'


)


select last_insert_id()


/* SignUpDAO.insertUserInfo */
insert into tb_user_info (
	user_no, user_id, user_name, user_gender, user_birthday, photo_file_no, tel1_no, tel2_no, user_url, agree01, agree02


	, approved_date, approved_user_no


) values (
	#{user_no}, #{user_id}, #{user_name}, #{user_gender}, #{user_birthday}, #{photo_file_no}, #{tel1_no}, #{tel2_no}, #{user_url}, #{agree01}, #{agree02}


	, now(), 1


)


/* SignUpDAO.insertHakwonTeacher */
insert into tb_hakwon_teacher (
	hakwon_no, user_no, approved_yn, reg_date
) values (
	#{hakwon_no}, #{user_no}, 'N', now()
)
