mapper.update:

/* UserDAO.updateUser */
update tb_user



		user_email = #{user_email},


		user_password = #{user_password}



where user_no = #{user_no}


/* UserDAO.updateUserInfo */
update tb_user_info



		user_name = #{user_name},


		user_gender = #{user_gender},


		user_birthday = #{user_birthday},


		tel1_no = #{tel1_no},


		tel2_no = #{tel2_no}



where user_no = #{user_no}


/* UserDAO.updateUserAlarmOff */
update tb_user_alarm set
	off_date = #{off_date}
where
	user_no = #{user_no}
