mapper.select:

/* eventDAO.eventUserCheck */
select
	count(1)
from
	tb_event_user a
where
	a.event_no = #{event_no}
	and a.user_no = #{user_no}

mapper.insert:

/* eventDAO.insertEventUser */
insert into tb_event_user (
	event_no, user_no, reg_date, recommend_user_no, user_info
) values (
	#{event_no}, #{user_no}, now(), #{recommend_user_no}, #{add_info}
)
